mapper.update:
UPDATE board
   SET title = #{title}
      ,writer = #{writer}
      ,content = #{content}
WHERE board_no = #{boardNo}

mapper.select:
SELECT views
FROM board
WHERE board_no = #{boardNo}

mapper.update:
UPDATE board
   SET views = views + #{count}
WHERE board_no = #{boardNo}

UPDATE board
   SET likes = #{count}
WHERE board_no = #{boardNo}
